package me.liuwentao.rpc.core.annotation;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * Created by liuwentao on 2021/7/12 10:28 * 放到服务实现类的基包 */@Target(ElementType.TYPE)@Retention(RetentionPolicy.RUNTIME)public @interface ServiceScan {    public String value() default "";}