package me.liuwentao.rpc.core.loadBalance;import com.alibaba.nacos.api.naming.pojo.Instance;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.List;import java.util.Random;/** * Created by liuwentao on 2021/7/5 17:15 */public class RandomLoadBalance implements LoadBalance{    private static final Logger logger = LoggerFactory.getLogger(RandomLoadBalance.class);    @Override    public Instance select(List<Instance> instanceList) {        logger.info("执行负载均衡，当前可用服务数：{}", instanceList.size());        if (instanceList.size() == 0) return null;        return instanceList.get((int)(Math.random() * instanceList.size()));    }}