package me.liuwentao.rpc.core.loadBalance;import com.alibaba.nacos.api.naming.pojo.Instance;import java.util.List;import java.util.Random;/** * Created by liuwentao on 2021/7/5 17:15 */public class RandomLoadBalance implements LoadBalance{    @Override    public Instance select(List<Instance> instanceList) {        return instanceList.get((int)(Math.random() * instanceList.size()));    }}