package me.liuwentao.rpc.core.loadBalance;import com.alibaba.nacos.api.naming.pojo.Instance;import java.util.List;/** * Created by liuwentao on 2021/7/7 17:31 */public class RoundRobinLoadBalance implements LoadBalance{    private int index = 0;    @Override    public Instance select(List<Instance> instanceList) {        if (index == instanceList.size()) {            index = 0;        }        return instanceList.get(index++);    }}